// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {CcipResponseVerifier} from "../CcipResponseVerifier.sol";
import {IBedrockProofVerifier} from "./IBedrockProofVerifier.sol";
import {convertEVMChainIdToCoinType} from "../../coinType/Ensip11CoinType.sol";

/**
 * @notice A type specific handler for verifying the validity of data returned by
 * a gateway that pulls data from Optimism Bedrock
 */ 
contract BedrockCcipVerifier is CcipResponseVerifier {
    IBedrockProofVerifier public immutable bedrockProofVerifier;
    address public immutable target;

    constructor(
        address owner,
        string memory graphQlUrl,
        IBedrockProofVerifier _bedrockProofVerifier,
        address _target
    ) CcipResponseVerifier(owner, graphQlUrl) {
        bedrockProofVerifier = _bedrockProofVerifier;
        target = _target;
    }

    /**
     * @notice Resolve a response with a proof
     * @dev This function allows resolving a response along with a proof provided by IBedrockProofVerifier.
     * @param response The response data along with the associated proof
     * @param extraData The original data passed to the request
     * @return The resolved response data encoded as bytes
     */
    function resolveWithProof(bytes calldata response, bytes calldata extraData) public view virtual override returns (bytes memory) {
        /*
         * @dev Decode the response and proof from the response bytes
         */
        (bytes memory responseEncoded, IBedrockProofVerifier.BedrockStateProof memory proof) = abi.decode(
            response,
            (bytes, IBedrockProofVerifier.BedrockStateProof)
        );
        /*
         * Revert if the proof target does not match the resolver. This is to prevent a malicious resolver from using a * proof intended for another address.
         */
        require(proof.target == target, "proof target does not match resolver");
        /*
         * bedrockProofVerifier.getProofValue(proof) always returns the packed result.
         * However, libraries like ethers.js expect the result to be encoded in bytes.
         * Hence, the gateway needs to encode the result before returning it to the client.
         * To ensure responseEncoded matches the value returned by bedrockProofVerifier.getProofValue(proof),
         * we need to check the layout of the proof and encode the result accordingly, so we can compare the two values * using the keccak256 hash.
         */

        require(
            proof.layout == 0
                ? keccak256(bedrockProofVerifier.getProofValue(proof)) == keccak256(responseEncoded)
                : keccak256(abi.encode(bedrockProofVerifier.getProofValue(proof))) == keccak256(responseEncoded),
            "proof does not match response"
        );

        return responseEncoded;
    }

    /**
     * @notice Get metadata about the CCIP Resolver
     * @dev This function provides metadata about the CCIP Resolver, including its name, coin type, GraphQL URL, storage type, and encoded information.
     * @return name The name of the resolver ("CCIP RESOLVER")
     * @return coinType Resolvers coin type (60 for Ethereum)
     * @return graphqlUrl The GraphQL URL used by the resolver
     * @return storageType Storage Type (0 for EVM)
     * @return storageLocation The storage identifier. For EVM chains, this is the address of the resolver contract.
     * @return context the owner of the name. Always returns address(0) since the owner is determined by the erc3668Resolver contract.
     */
    function metadata(
        bytes calldata
    ) external view override returns (string memory, uint256, string memory, uint8, bytes memory, bytes memory) {
        return (
            "Optimism Goerli", // the name of the resolver
            convertEVMChainIdToCoinType(420), // coinType according to ENSIP-11 for chain id 420
            this.graphqlUrl(), // the GraphQL Url
            uint8(0), // storage Type 0 => EVM
            abi.encodePacked(address(target)), // storage location => resolver address
            abi.encodePacked(address(0)) // context => l2 resolver address
        );
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import {IBedrockProofVerifier, IL2OutputOracle} from "./IBedrockProofVerifier.sol";

import {RLPReader} from "@eth-optimism/contracts-bedrock/contracts/libraries/rlp/RLPReader.sol";
import {Hashing} from "@eth-optimism/contracts-bedrock/contracts/libraries/Hashing.sol";

import {Lib_SecureMerkleTrie} from "@eth-optimism/contracts/libraries/trie/Lib_SecureMerkleTrie.sol";

import {BytesLib} from "solidity-bytes-utils/contracts/BytesLib.sol";

contract BedrockProofVerifier is IBedrockProofVerifier {
    IL2OutputOracle public immutable l2OutputOracle;

    constructor(address _l2OutputOracle) {
        l2OutputOracle = IL2OutputOracle(_l2OutputOracle);
    }

    /**
     * @notice Get the proof value for the provided BedrockStateProof
     * @dev This function validates the provided BedrockStateProof and returns the value of the slot or slots included in the proof.
     * @param proof The BedrockStateProof struct containing the necessary proof data
     * @return result The value of the slot or slots included in the proof
     */
    function getProofValue(BedrockStateProof memory proof) public view override returns (bytes memory) {
        /*
         * Validate the provided output root is valid
         * See https://github.com/ethereum-optimism/optimism/blob/4611198bf8bfd16563cc6bdf49bb35eed2e46801/packages/contracts-bedrock/contracts/L1/OptimismPortal.sol#L261
         */
        require(
            l2OutputOracle.getL2Output(proof.l2OutputIndex).outputRoot == Hashing.hashOutputRootProof(proof.outputRootProof),
            "Invalid output root"
        );

        bytes memory result = getMultipleStorageProofs(proof);

        /*
         * If the storage layout is fixed, the result doesn't need to be trimmed
         */
        if (proof.layout == 0) {
            return result;
        }
        return trimResult(result, proof.length);
    }

    /**
     * @notice Get the storage root for the provided BedrockStateProof
     * @dev This private function retrieves the storage root based on the provided BedrockStateProof.
     * @param proof The BedrockStateProof struct containing the necessary proof data
     * @return The storage root retrieved from the provided state root
     */
    function getStorageRoot(BedrockStateProof memory proof) private pure returns (bytes32) {
        (bool exists, bytes memory encodedResolverAccount) = Lib_SecureMerkleTrie.get(
            abi.encodePacked(proof.target),
            proof.stateTrieWitness,
            proof.outputRootProof.stateRoot
        );
        /*
         * The account storage root has to be part of the provided state root
         * It might take some time for the state root to be posted on L1 after the transaction is included in a block
         * Until then, the account might not be part of the state root
         */
        require(exists, "Account is not part of the provided state root");
        RLPReader.RLPItem[] memory accountState = RLPReader.readList(encodedResolverAccount);
        return bytes32(RLPReader.readBytes(accountState[2]));
    }

    /**
     * The slot values are padded with 0 so that they are 32 bytes long. This padding has to be returned so the returned value is the same length as the original value
     * @param result The concatenated result of all storage slots
     * @param length The length of the original value
     */
    function trimResult(bytes memory result, uint256 length) private pure returns (bytes memory) {
        if (length == 0) {
            return result;
        }
        return BytesLib.slice(result, 0, length);
    }

    /**
     * @notice Get multiple storage proofs for the provided BedrockStateProof
     * @dev Dynamic Types like bytes, strings, or arrays are spread over multiple storage slots. This proves every storage slot the dynamic type contains and returns the concatenated result
     * @param proof The BedrockStateProof struct containing the necessary proof data
     * @return result The concatenated storage proofs for the provided BedrockStateProof
     */
    function getMultipleStorageProofs(BedrockStateProof memory proof) private pure returns (bytes memory) {
        bytes memory result = new bytes(0);
        /*
         * The storage root of the account
         */
        bytes32 storageRoot = getStorageRoot(proof);

        /*
         * For each sub storage proof, we are proving that the slot is included in the account root of the account
         */
        for (uint256 i = 0; i < proof.storageProofs.length; i++) {
            bytes memory slotValue = getSingleStorageProof(storageRoot, proof.storageProofs[i]);
            /*
             * Attach the current slot to the result
             */
            result = BytesLib.concat(result, slotValue);
        }
        return result;
    }

    /**
     * @notice prove whether the provided storage slot is part of the storageRoot
     * @param storageRoot the storage root for the account that contains the storage slot
     * @param storageProof the StorageProof struct containing the necessary proof data
     * @return the retrieved storage proof value or 0x if the storage slot is empty
     */
    function getSingleStorageProof(bytes32 storageRoot, StorageProof memory storageProof) private pure returns (bytes memory) {
        (bool storageExists, bytes memory retrievedValue) = Lib_SecureMerkleTrie.get(
            abi.encodePacked(storageProof.key),
            storageProof.storageTrieWitness,
            storageRoot
        );
        /*
         * this means the storage slot is empty. So we can directly return 0x without RLP encoding it.
         */
        if (!storageExists) {
            return retrievedValue;
        }
        return RLPReader.readBytes(retrievedValue);
    }
}

